pragma solidity ^0.4.24;

contract QAOracle {
  address internal oracleAddress;
  mapping (address => Question) questions;

  event debug( address sender, uint256 value );

  struct Question {
    address qAddress;
    string qText;
    uint256 bounty;
    string answer;
  }

  constructor () public {
    oracleAddress = msg.sender;
  }

  modifier onlyOracle() {
    require(msg.sender == oracleAddress);
    _;
  }

  function addQuestion(address qAddr, string question) public onlyOracle payable {
    require (msg.value > 0);
    emit debug( qAddr, msg.value );
    questions[qAddr] = Question(qAddr, question, msg.value, '');
  }

  function getQuestion(address qAddr) public view returns(string) {
    return questions[qAddr].qText;
  }

  function getQuestionBounty(address qAddr) public view returns(uint256) {
    return questions[qAddr].bounty;
  }

  function getQuestionAnswer(address qAddr) public view returns(string) {
    return questions[qAddr].answer;
  }

  function answerQuestion(address qAddr, address aAddr, string answer) public onlyOracle {
    Question storage question = questions[qAddr];
    require (bytes(question.answer).length == 0);
    emit debug (aAddr, question.bounty);
    question.answer = answer;
    aAddr.transfer(question.bounty);
  }
}
